class Solution:
    def longestPalindrome(self, s: str) -> int:
        hmap = {}
        for c in s:
            hmap[c] = hmap.get(c,0)+1
        maxLen = 0
        oddTaken = False
        
        print(hmap)
        
        for k, v in hmap.items():
            if v%2 == 0:
                maxLen += v
            elif v%2:
                maxLen += (v-1)
                if not oddTaken:
                    maxLen+=1
                    oddTaken = True        
        return maxLen

